ee:message.ee:set-payload:
%dw 2.0 output text/csv separator=","
var studentInfo = { "ID": payload.id, "Name": payload.name, "Email": payload.email, "Final_Section": payload.current_section, "Total Score": payload.cumulated_score, "Passed": payload.passed default false }
var emptyRow = { "ID": null, "Name": null, "Email": null, "Final_Section": null, "Total Score": null, "Passed": null }
// header for marks section var marksHeaderRow = { "Day of Course": "Day of Course", "Section": "Section", "Subject 1": "Subject 1", "Subject 2": "Subject 2", "Subject 3": "Subject 3", "Day Total": "Day Total", "Weighted Score": "Weighted Score", "Created Date": "Created Date" }
var marksData = vars.marks map (m) -> { "Day of Course": m.day_of_course, "Section": m.section, "Subject 1": m.subject_1, "Subject 2": m.subject_2, "Subject 3": m.subject_3, "Day Total": m.days_total_marks, "Weighted Score": m.weighted_score, "Created Date": m.created_date }
---
[ studentInfo, emptyRow, emptyRow, emptyRow ] ++ [ marksHeaderRow ] ++ marksData

%dw 2.0
output multipart/form-data
---
{
  parts: {
    fileName: {
      content: "reports/"++ (vars.marks[0].id) ++ ".csv",
      headers: {
        "Content-Disposition": "form-data; name=\"fileName\"",
        "Content-Type": "text/plain"
      }
    },
    file: {
      content: payload,
      headers: {
        "Content-Disposition": "form-data; name=\"file\"; filename=\"" ++ vars.marks[0].id ++ ".csv\"",
        "Content-Type": "text/csv"
      }
    }
  }
}
